def getTokenPrice():
    arb = "https://speedy-nodes-nyc.moralis.io/80e8714f0b9b5a203c20b5e8/arbitrum/mainnet"
    web3 = Web3(Web3.HTTPProvider(arb))
    #arb/sushi router
    arbRouterContractAddress = web3.toChecksumAddress('0x1b02da8cb0d097eb8d57a175b88c7d8b47997506')
    ETHTokenAddress = web3.toChecksumAddress("0x82af49447d8a07e3bd95bd0d56f35241523fbab1") #weth
    TokenAddress  = web3.toChecksumAddress("0x539bde0d7dbd336b79148aa742883198bbf60342") ##magic
    usdc = web3.toChecksumAddress('0xff970a61a04b1ca14834a43f5de4533ebddb5cc8')
    magictosell = web3.toWei('1', 'Ether')
    amountout = ''
    exchange_contract = web3.eth.contract(address=arbRouterContractAddress, abi=sushiabi)
    amount = exchange_contract.functions.getAmountsOut(magictosell,[TokenAddress,ETHTokenAddress,usdc]).call()
    amountout = amount[2]/1000000
   
    return round(amountout,3)


def buildFloorQuery():
    query = gql(
        f"""
        {{
        collections {{
            id
            address
            name
            totalListings
            floorPrice
        }}
        }}
        """
        )
    return query

def getSmolFloorPrice(collection):
    transport = AIOHTTPTransport(url="https://api.thegraph.com/subgraphs/name/wyze/treasure-marketplace")

    # Create a GraphQL client using the defined transport
    client = Client(transport=transport, fetch_schema_from_transport=True)
    result = client.execute(buildFloorQuery())
    pprint(result)
    smolbrains = [obj for obj in result['collections'] if(obj['name'] == collection)]
    floor = int(smolbrains[0]['floorPrice'])/1000000000000000000
    #print(f"floor is {floor}")
    return floor

def getPriceGraph(ticker):
    query = gql(
        f"""
        {{
        pairs(where:{{name:"{ticker}-USDT"}}){{
            name
            token1Price
            }}
        }}
        """
        )
    transport = AIOHTTPTransport(url="https://api.thegraph.com/subgraphs/name/sushiswap/arbitrum-exchange")
    client = Client(transport=transport, fetch_schema_from_transport=True)
    result = client.execute(query)
    pprint(result)
    return(round(float(result["pairs"][1]["token1Price"]),3))

